import React from 'react';
import Canvas from './canvas';

class Purchase extends React.Component{
  constructor(props){
  super(props);
  this.state = {purchaseType:null}
  this.handleSelectRadio = this.handleSelectRadio.bind(this);
}

  handleSelectRadio (event){
    this.setState({purchaseType:event.target.value});
  }

  render(){
    //console.log(this.state.purchaseType);
    return (
      <div>
      <h1>Purchase Authenticity</h1>
      <br />
      <h4>Almost there! Now you will choose your purchase options and complete the transaction for punk #{this.props.punkId}.</h4>
    <Canvas
    punkId={this.props.punkId}
    />
    <p>There are ways to proceed. You may purchase a 12"x12" (30.5x30.5cm) high quality digital print with attached authentication NFC
    or if you already have a nice print you can simply buy the NFC sticker for authentications. Please select one:</p>
    <label><input type="radio" name="selectOption" value="print" onChange={this.handleSelectRadio} />Purchase Print+NFC</label><br />
    <label><input type="radio" name="selectOption" value="NFC" onChange={this.handleSelectRadio} />Purchase NFC Only</label>

    {this.state.purchaseType &&
      <div>
      <h4>Great you're purchasing a {this.state.purchaseType}</h4>
      </div>
    }
    </div>
  )
  }

}

export default Purchase;
