    <div className="App">
      <PrinterAddress
        drizzle={this.props.drizzle}
        drizzleState={this.state.drizzleState}
      />
      <PricePerPrint
        drizzle={this.props.drizzle}
        drizzleState={this.state.drizzleState}
      />
      <UpdatePricePerPrint
      drizzle={this.props.drizzle}
      drizzleState={this.state.drizzleState}
      />
      <PurchasePrint
      drizzle={this.props.drizzle}
      drizzleState={this.state.drizzleState}
      />
    </div>
  );
}
}export default App;
